import React from "react";

function UserInput(){

    function submiHandler(event){
        event.preventDefault();


    }

    function resetHandler(event){

    }

    //Since inputChangeHandler is passed as a value to onChange, will only be executed by react whenever the value in this input changes. So therefore, indirectly inputChangeHandler will only be executed when the input value changes.
    function inputChangeHandler(input, value){
        
    }

    return(
        <form onSubmit={submiHandler} className="form">

            <div className="input-group">
                <p>
                    <label htmlFor="current-savings">Current Savings ($)</label>
                    <input onChanges={function(event){inputChangeHandler("current-savings", event.target.value)}} type="number" id="current-savings" />
                </p>
                <p>
                    <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
                    <input type="number" id="yearly-contribution" />
                </p>
            </div>
            
            <div className="input-group">
                <p>
                    <label htmlFor="expected-return">Expected Interest (%, per year)</label>
                    <input type="number" id="expected-return" />
                </p>
                <p>
                    <label htmlFor="duration">Investment Duration (years)</label>
                    <input type="number" id="duration" />
                </p>
            </div>

            <p className="actions">
                <button onClick={resetHandler} type="reset" className="buttonAlt">Reset</button>
                <button type="submit" className="button">Calculate</button>
            </p>
        </form>
    )
};

export default UserInput;
