import React, {useState} from "react";

function UserInput(){

    const [userInput, setUserInput] = useState({
        currentSavings: 10000,
        yearlyContribution: 1200,
        expectedReturn: 7,
        duration: 10,
    });

    function submiHandler(event){
        event.preventDefault();


    }

    function resetHandler(event){

    }

    //Since inputChangeHandler is passed as a value to onChange, will only be executed by react whenever the value in this input changes. So therefore, indirectly inputChangeHandler will only be executed when the input value changes. We must use arrow functions in this function because we have to pass in two arguments (input and value) instead of only one. That's to say, if we wouldn't use arrow functions, then we would be just passin in one argument, which will be "event".
    //An arrow function prevents the inputChangeHandler() function from executing after the component is mounted. It onlys execute when users add a new character.
    function inputChangeHandler(input, value){
        
    }


    return(
        <form onSubmit={submiHandler} className="form">

            <div className="input-group">
                <p>
                    <label htmlFor="current-savings">Current Savings ($)</label>
                    <input onChanges={(event) => inputChangeHandler("current-savings", event.target.value)} type="number" id="current-savings" />
                </p>
                <p>
                    <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
                    <input onChanges={(event) => inputChangeHandler("yearly-contribution", event.target.value)} type="number" id="yearly-contribution" />
                </p>
            </div>
            
            <div className="input-group">
                <p>
                    <label htmlFor="expected-return">Expected Interest (%, per year)</label>
                    <input onChanges={(event) => inputChangeHandler("expected-return", event.target.value)} type="number" id="expected-return" />
                </p>
                <p>
                    <label htmlFor="duration">Investment Duration (years)</label>
                    <input onChanges={(event) => inputChangeHandler("duration", event.target.value)} type="number" id="duration" />
                </p>
            </div>

            <p className="actions">
                <button onClick={resetHandler} type="reset" className="buttonAlt">Reset</button>
                <button type="submit" className="button">Calculate</button>
            </p>
        </form>
    )
};

export default UserInput;
